function assign(graph, options = {}) {
  const attribute = options.communityAttribute || 'community';
  graph.forEachNode(node => {
    graph.setNodeAttribute(node, attribute, 0);
  });
}

function simpleCommunities(graph) {
  const result = {};
  graph.forEachNode(node => {
    result[node] = 0;
  });
  return result;
}

function detailed(graph, options) {
  return {
    communities: simpleCommunities(graph, options),
    modularity: 0
  };
}

export default function louvain(graph, options) {
  return simpleCommunities(graph, options);
}

louvain.assign = assign;
louvain.detailed = detailed;


